mapper.insert:
insert into course(course_no, course_name, credit, course_hours, student_number)
    value (#{course.courseNo}, #{course.courseName}, #{course.credit}, #{course.courseHours},
    #{course.studentNumber});

mapper.select:
select id, course_no, course_name, credit, course_hours, student_number
from course
where course_no = #{courseNo};

select id, course_no, course_name, credit, course_hours, student_number
from course


        credit=#{query.credit},


        and course_hours=#{query.courseHours}


        and student_number=#{query.studentNumber}
