mapper.select:
select count(*)
from course_selection
where student_no = #{studentNo}
  and course_plan_no = #{coursePlanNo}

select count(*)
from course_selection
where course_plan_no = #{coursePlanNo}

SELECT s.student_name , s.student_no , c.course_name , c.course_no ,
t.teacher_name , t.teacher_no , c.credit , c.course_hours
from student s
JOIN course_selection cs on s.student_no = cs.student_no
JOIN course_plan cp ON cs.course_plan_no = cp.course_plan_no
JOIN course c ON c.course_no = cp.course_no
join teacher t on t.teacher_no = cp.teacher_no



        cs.student_no = #{query.studentNo}



        and cp.teacher_no = #{query.teacherNo}



        and c.course_name = #{query.courseName}
